#include <stdio.h>

int sort(int a[10])
{
	int i,j;
	
	for (i=0; i<10; i++)
	{
		for (j=9; j>i; j--)
		{
			if (a[i] < a[j])
			{
				a[i] = a[i] + a[j];
				a[j] = a[i] - a[j];
				a[i] = a[i] - a[j];
			}
		}
	}
	
	return 0;
}

int sort_n(int a[], int n)
{
	int i,j;
	
	for (i=0; i<n; i++)
	{
		for (j=n-1; j>i; j--)
		{
			if (a[i] < a[j])
			{
				a[i] = a[i] + a[j];
				a[j] = a[i] - a[j];
				a[i] = a[i] - a[j];
			}
		}
	}
	
	return 0;
}

int sort_n_type(int a[], int n, char style)
{
	int i,j;
	
	if (style != 'a' && style !='d')
	{
		printf("The sort style [%c] error!", style);
		return -1;
	}
	
	if (style == 'a')
	{
		int i,j;
	
		for (i=0; i<n; i++)
		{
			for (j=n-1; j>i; j--)
			{
				if (a[i] > a[j])
				{
					a[i] = a[i] + a[j];
					a[j] = a[i] - a[j];
					a[i] = a[i] - a[j];
				}
			}
		}
	}
	else
	{
		int i,j;
	
		for (i=0; i<n; i++)
		{
			for (j=n-1; j>i; j--)
			{
				if (a[i] < a[j])
				{
					a[i] = a[i] + a[j];
					a[j] = a[i] - a[j];
					a[i] = a[i] - a[j];
				}
			}
		}
	}
	
	return 0;
}

static void print(int a[], int n)
{
	int i;
	
	printf("---------\n");
	
	for (i=0; i<n; i++)
	{
		printf("No.%d element is %d\n", i+1, a[i]);
	}
}

int main(int argc, char *argv[])
{
	int a[]={93, 91, 93, 95, 96, 98, 99, 62, 78, 82, 69, 99};
	int b[]={93, 91, 93, 95, 96, 98, 99, 62, 78, 82};
	int n = sizeof(a)/sizeof(int);
	
	/* print a[] */
	print(a, n);
	
	/* use sort */
	if (!sort(a))
		print(a,n);
	
	/* use sort_n */
	if (!sort_n(a, n))
		print(a, n);
	
	/* use sort_n_type by 'a' */
	if (!sort_n_type(a, n, 'a'))
		print(a, n);
	
	/* use sort_n_type by 'd' */
	if (!sort_n_type(a, n, 'd'))
		print(a, n);
	
	return 0;
}
